def term_parl(x):
    if (x >= pd.to_datetime('2013-01-15')) & (x <= pd.to_datetime('2015-05-18')):
        return "2010-2015"
    elif (x >= pd.to_datetime('2015-05-18')) & (x <=pd.to_datetime('2017-06-20')):
        return "2015-2017"
    elif (x >= pd.to_datetime('2017-06-21')) & (x <=pd.to_datetime('2019-12-18')):
        return "2017-2019"
    elif (x >= pd.to_datetime('2019-12-19')) :
        return "2019-now"
